function Confirm(){
	this.url='';
	this.name='';
}

Confirm.prototype.delconfirm=function(url,name){
	this.url=url;
	this.name=name;
	var _this=this;
	$.layer({
	    shade: [0],
	    area: ['auto','auto'],
	    dialog: {
	        msg: '您确定要删除："'+_this.name+'"吗?',
	        btns: 2,                    
	        type: 4,
	        btn: ['确定','取消'],
	        yes: function(){
	        	window.location.href=_this.url;     
	        }, no: function(){
	            layer.alert('您取消了操作不做任何操作!',1)
	        }
	    }
	});
}